mapper.select:
SELECT COUNT(*)
FROM CARTS
WHERE 1 = 1
  AND USER_ID = #{userId}
  AND OPT_NO = #{optNo}

mapper.delete:
DELETE
FROM CARTS
WHERE USER_ID = #{userId}
  AND OPT_NO = #{optNo}

mapper.update:
UPDATE CARTS
SET NOW_CNT= #{cnt}
WHERE USER_ID = #{userId}
  AND OPT_NO = #{optNo}

UPDATE CARTS
SET CART_CHECK=#{check}
WHERE USER_ID = #{userId}
  AND OPT_NO = #{optNo}

mapper.select:
SELECT BUY_NO
FROM BUY_HISTORY
WHERE USER_ID = #{user.userId}
  AND BUY_USER_NAME = #{user.userName}
  AND USER_ZIP = #{user.userZip}
  AND USER_WANT = #{userWant}
  AND TOTAL_PRICE = #{totalPrice}

SELECT COUNT(*)
FROM CARTS
WHERE user_id = #{userId}

mapper.update:
UPDATE PROD_OPTIONS
SET OPT_STOCK=
  (
    SELECT (OPT_STOCK - #{nowCnt})
    FROM PROD_OPTIONS
   WHERE OPT_NO = #{optNo}
  AND OPT_PROD_NO = #{prodNo}
)
WHERE OPT_NO = #{optNo}
AND OPT_PROD_NO = #{prodNo}
