mapper.update:
UPDATE BUY_OPT
SET REVI_NO= #{reviNo}
WHERE  USER_ID =#{userId}
  AND PROD_NO = #{parentNo}
  AND  USER_BUY_NO = #{buyNo}
  AND  OPT_NO = #{optNo}

UPDATE BUY_OPT
SET REVI_NO= 0
WHERE  USER_ID =#{userId}
  AND PROD_NO = #{parentNo}
  AND  USER_BUY_NO = #{buyNo}
  AND  OPT_NO = #{optNo}

mapper.select:
SELECT OPT_NO, OPT_FIRST, OPT_SECOND, OPT_STOCK, OPT_PROD_NO,OPT_DEL_YN
FROM PROD_OPTIONS
WHERE OPT_NO = #{optNo}

SELECT OPT_NO, OPT_FIRST, OPT_SECOND, OPT_STOCK, OPT_PROD_NO,OPT_DEL_YN
FROM PROD_OPTIONS
WHERE OPT_PROD_NO = #{prodNo}
AND OPT_FIRST = #{first}
AND OPT_SECOND = #{second}
  AND OPT_DEL_YN = 'N'
