mapper.select:
SELECT OPT_NO, OPT_FIRST, OPT_SECOND, OPT_STOCK, OPT_PROD_NO
FROM OPTIONS
WHERE OPT_PROD_NO = #{prodNo}

SELECT OPT_NO, OPT_FIRST, OPT_SECOND, OPT_STOCK, OPT_PROD_NO
FROM OPTIONS
WHERE OPT_NO = #{optNo}

SELECT OPT_NO, OPT_FIRST, OPT_SECOND, OPT_STOCK, OPT_PROD_NO
FROM OPTIONS
WHERE OPT_PROD_NO = #{prodNo}
AND OPT_FIRST = #{first}
AND OPT_SECOND = #{second}
