mapper.update:
UPDATE REVIEWS
SET REVI_CONTENT=#{reviContent}
  , REVI_STAR=#{reviStar}
  , REVI_MOD_DATE=SYSDATE
  , REVI_IP='111.11.11.1'
WHERE REVI_NO = #{reviNo}

mapper.select:
SELECT REVI_NO
FROM REVIEWS
WHERE REVI_USER_ID = #{userId}
  AND REVI_PARENT_NO = #{parentNo}
  AND BUY_NO = #{buyNo}
  AND OPT_NO = #{optNo}
